import React, { useState, useRef, useEffect } from 'react';
import * as d3 from 'd3';

// Data Should be in object form {x: Val, y: Val}, ...
// Otherwise it will not be seen by generator

function LineChart(props) {
    const { data } = props;

    const svgRef = useRef();
    useEffect(() => {
        const layout = {
            width: 300,
            height: 200
        };
        const svg = d3.select(svgRef.current)
            .attr('width', width)
            .attr('height', height)
            .style('background', '#FAF9F6');

        // creates scales for graph
        const graph_details = {
            xScale: d3.scaleTime().range([0, layout.width]),
            yScale: d3.scaleLinear().range([layout.height, 0]),
            lineGenerator: d3.line()
        };

        // Sets Top and Bottom Values for X and Y Axis
        graph_details.xScale.domain(d3.extent(data, function(d) { return d.date; }));
        graph_details.yScale.domain(0, 100);

        // Tells generator where to find the data
        graph_details.lineGenerator.x(d => graph_details.xScale(d["x"]));
        graph_details.lineGenerator.y(d => graph_details.yScale(d["y"]));

        // Generates Graph
        const [lineData, setLineData] = useState(() => {
            graph_details.lineGenerator(data);
        });


    });



    return <svg 
        className='conditions__graph'
        width={"100%"}
        height={layout.height}
        viewBox={`0 0 ${layout.width} ${layout.height}`}
        preserveAspectRatio = 'none'>
            <path className='graph__data' d={ lineData } />
        </svg>
}

export default LineChart;
