import {useState} from 'react';
import resortButton from './resort_button';
const directory = require('../config/resorts.json');


function ResortList( props ) {
    const [ display, setDisplay] = useState('none');

    resorts = props.resorts;
    listitems = resorts.map(resort => <resortButton resort={resort} />)

    function handleClick() {
        // This just Toggles display
        if (display === 'none') {
            setDisplay('block');
        }
        else {
            setDisplay('none');
        }
    }

    return <div style={{display:display}} className="resorts__button">
            <ul>{listitems}</ul>
    </div>
}

export default ResortList;
