import React, { Component } from 'react';
import Navbar from './components/NavBar';
import Conditions from './components/conditions';
import { Data } from './components/graph';
import { Line } from 'react-@reactchartjs/react-chart.js-2';


class App extends Component {
    state = {
            resorts : ['Okemo', 'Snow', 'Hunter', 'Sunapee', 'Stowe'],
            resort : 'Okemo'
        };

    render() { 
        return (
            <div className='main__wrap'>
                <Navbar />
                <div className='conditions__box'>
                    <h2>Current Conditions</h2>
                    <Conditions resort = {this.state.resort} />
                </div>
                <div className='graph__box'>
                    <h2>Conditions this year</h2>
                    <Line data={Data}/>
                </div>
            </div>

        );
    };
}

export default App;
